package com.aua.museum.booking.domain;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.*;class UserTest {    User user = new User();    @Test    void addRole() {        user.setRole(Role.USER_ROLE);        assertEquals(user.getRole(), Role.USER_ROLE);        user.setRole(Role.ADMIN_ROLE);        assertEquals(user.getRole(), Role.ADMIN_ROLE);    }}