package com.aua.museum.booking.mapping;import com.aua.museum.booking.domain.Role;import com.aua.museum.booking.domain.User;import com.aua.museum.booking.dto.QuestionDetailsDto;import com.aua.museum.booking.dto.UserDto;import org.junit.jupiter.api.Test;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import java.util.ArrayList;import java.util.List;import static org.junit.jupiter.api.Assertions.assertEquals;@SpringBootTestclass UserMappingTest {    @Autowired    private UserMapperDecorator userMapper;    @Test    void userRegistrationDtoToUser() {        UserDto userDto = new UserDto();        userDto.setFullName("James Smith");        userDto.setPassword("12345678");        userDto.setUsername("user012364");        userDto.setEmail("jamie.smith@hotmail.com");        userDto.setPhone("+374356985478");        QuestionDetailsDto firstQuestionDetailsDto = new QuestionDetailsDto();        firstQuestionDetailsDto.setQuestionId(1);        firstQuestionDetailsDto.setAnswer("Merchada");        QuestionDetailsDto secondQuestionDetailsDto = new QuestionDetailsDto();        secondQuestionDetailsDto.setQuestionId(5);        secondQuestionDetailsDto.setAnswer("Aso");        List<QuestionDetailsDto> questions = new ArrayList<>();        questions.add(firstQuestionDetailsDto);        questions.add(secondQuestionDetailsDto);        userDto.setQuestionsDetails(questions);        Role role = Role.USER_ROLE;        User user = userMapper.toEntity(userDto);        assertEquals(userDto.getFullName(), user.getFullName());        assertEquals(userDto.getPassword(), user.getPassword());        assertEquals(userDto.getUsername(), user.getUsername());        assertEquals(userDto.getEmail(), user.getEmail());        assertEquals(userDto.getPhone(), user.getPhone());        assertEquals(user.getRole(), role);    }}